/******************************************* To catch the file name associate with * the .file compiler instrc. */char	VectInclFileName [IndxMaxIFN-1][32];   /* Vector */int		CountCharIFN = 0;  /* To fill a string */int		IndxVectFN = 0;	   /* Index of the vector *//*******************************************/					/***************************************************			* ### take the file name in relation with a .file  			* compiler directive; to fix an old bug. This option 			* is in use when the compiler deal with an appended 			* file coming from many other include files.			*/						VectInclFileName[IndxVectFN][CountCharIFN] = ch;			++CountCharIFN;						/* Ponctuate the string */			if  (VectInclFileName[IndxVectFN][CountCharIFN-1] == '"')			{				/* Ponctuate the string */				VectInclFileName [IndxVectFN] [CountCharIFN-1]  = '\0'; 								iowprintf ("%s %s %s", "Load file name vect with : ", VectInclFileName[IndxVectFN], "\n");												CountCharIFN = 0;  /* To start the next string */								if  (IndxVectFN < IndxMaxIFN)					++IndxVectFN;				else				{				 	iowprintf ("Too many  '.file'  in the appended file\n");					iowprintf ("therefore : the errors messages will have\n");					iowprintf ("an incorrect file name identification\n");				}		    }			/*			***************************************************/