#include "types.r"/* These define's are used in the MENU resources to disable specific   menu items. */#define AllItems	0b1111111111111111111111111111111	/* 31 flags */#define MenuItem1	0b0000000000000000000000000000001#define MenuItem2	0b0000000000000000000000000000010#define MenuItem3	0b0000000000000000000000000000100#define MenuItem4	0b0000000000000000000000000001000#define MenuItem5	0b0000000000000000000000000010000#define MenuItem6	0b0000000000000000000000000010000#define MenuItem7	0b0000000000000000000000000100000#define MenuItem8	0b0000000000000000000000001000000#define MenuItem9	0b0000000000000000000000010000000#define MenuItem10	0b0000000000000000000000100000000resource 'MENU' (128, "Apple") {	128,	textMenuProc,	AllItems & ~MenuItem2,	enabled,	apple,	{	/* array: 2 elements */		/* [1] */		"About…", noIcon, "", "", plain,		/* [2] */		"-", noIcon, "", "", plain	}};resource 'MENU' (129, "File") {	129,	textMenuProc,	AllItems & ~MenuItem2 & ~MenuItem5 ,		enabled,	"File",	{			/* [1] */		"Assemble…", noIcon, "O", "", plain,		/* [2] */		"-", noIcon, "", "", plain,		/* [3] */		"Save Output Window", noIcon, "S", "", plain,		/* [4] */		"Clear Output Window", noIcon, "", "", plain,		/* [5] */		"-", noIcon, "", "", plain,		/* [6] */		"Quit", noIcon, "Q", "", plain	}};resource 'MENU' (130, "Edit") {	130,	textMenuProc,	AllItems & ~MenuItem2,		0,	"Edit",	{	/* array: 5 elements */		/* [1] */		"Undo", noIcon, "Z", "", plain,		/* [2] */		"-", noIcon, "", "", plain,		/* [3] */		"Cut", noIcon, "X", "", plain,		/* [4] */		"Copy", noIcon, "C", "", plain,		/* [5] */		"Paste", noIcon, "V", "", plain	}};resource 'MENU' (131, "Transfer") {	131,	textMenuProc,	AllItems & ~MenuItem3,		enabled,	"Transfer",	{	/* array: 5 elements */		/* [1] */		"MacVAX", noIcon, "", "", plain,		/* [2] */		"Edit", noIcon, "", "", plain,		/* [3] */		"-", noIcon, "", "", plain,		/* [4] */		"Other", noIcon, "", "", plain	}};